mapper.select:
SELECT PERIOD_FIXED_MONTH as fixedMonth FROM TB_DPST_PROD_PERIOD
WHERE period_dpst_id=#{dpstId}
order by PERIOD_FIXED_MONTH asc

mapper.insert:
INSERT INTO TB_DPST_ACCT_HDR ( DPST_HDR_DPST_ID, DPST_HDR_PW, DPST_HDR_CUST_CODE, DPST_HDR_MONTH, DPST_HDR_START_DY, DPST_HDR_FIN_DY, DPST_HDR_CURRENCY, DPST_HDR_BALANCE, DPST_HDR_INTEREST, DPST_HDR_STATUS, DPST_HDR_LINKED_ACCT_NO, DPST_HDR_AUTO_RENEW_YN, DPST_HDR_PART_WDRW_CNT, DPST_HDR_INFO_AGREE_YN, DPST_HDR_INFO_AGREE_DT, DPST_HDR_CONTRACT_DT, DPST_HDR_RATE, DPST_HDR_CURRENCY_EXP, DPST_HDR_EXP_ACCT_NO, DPST_HDR_LINKED_ACCT_TYPE
, DPST_HDR_AUTO_RENEW_TERM , DPST_HDR_AUTO_RENEW_CNT
) VALUES ( #{dpstHdrDpstId}, #{dpstHdrPw}, #{dpstHdrCustCode}, #{dpstHdrMonth}, #{dpstHdrStartDy}, #{dpstHdrFinDy}, #{dpstHdrCurrency}, #{dpstHdrBalance}, #{dpstHdrInterest}, #{dpstHdrStatus}, #{dpstHdrLinkedAcctNo}, #{dpstHdrAutoRenewYn, jdbcType=CHAR}, #{dpstHdrPartWdrwCnt}, #{dpstHdrInfoAgreeYn}, #{dpstHdrInfoAgreeDt}, SYSDATE, #{dpstHdrRate}, #{dpstHdrCurrencyExp}, #{dpstHdrExpAcctNo}, #{dpstHdrLinkedAcctType}
, #{dpstHdrAutoRenewTerm} , #{dpstHdrAutoRenewCnt}
)

mapper.select:
SELECT *
FROM TB_DPST_ACCT_HDR
WHERE DPST_HDR_CUST_CODE = #{dpstHdrCustCode}
AND DPST_HDR_DPST_ID = #{dpstHdrDpstId}
ORDER BY DPST_HDR_CONTRACT_DT DESC
FETCH FIRST 1 ROWS ONLY

mapper.insert:
INSERT INTO TB_DPST_ACCT_DTL ( DPST_DTL_TYPE, DPST_DTL_APPLIED_RATE, DPST_DTL_AMOUNT, DPST_TRAN_DT, DPST_DTL_HDR_NO, DPST_DTL_ESIGN_YN
, DPST_DTL_ESIGN_DT
) VALUES ( #{dpstDtlType}, #{dpstDtlAppliedRate}, #{dpstDtlAmount}, SYSDATE, #{dpstDtlHdrNo}, #{dpstDtlEsignYn}
, #{dpstDtlEsignDt}
)

mapper.select:
select acct_balance
from tb_cust_acct
where acct_no = #{acctNo}

select bal_balance
from TB_FRGN_ACCT_BALANCE
where bal_no = #{balNo}

mapper.update:
UPDATE TB_CUST_ACCT
SET ACCT_BALANCE = #{acctBalance}
WHERE ACCT_NO = #{acctNo}

UPDATE TB_FRGN_ACCT_BALANCE
SET BAL_BALANCE = #{balBalance}
WHERE BAL_NO = #{balNo}
