mapper.select:
SELECT MAX(term_order)
FROM TB_TERMS_MASTER
WHERE term_cate = #{cate}

SELECT *
FROM TB_TERMS_HIST
WHERE thist_term_cate = #{termCate}
  AND thist_term_order = #{termOrder}
ORDER BY thist_version DESC
    FETCH FIRST 1 ROW ONLY

mapper.update:
UPDATE TB_TERMS_MASTER
SET term_title = #{termTitle}
WHERE term_cate = #{termCate}
  AND term_order = #{termOrder}

mapper.select:
SELECT *
FROM TB_TERMS_MASTER
WHERE term_cate = #{cate}
  AND term_order = #{order}
