mapper.select:
select *
from tb_cust_info
where CUST_ID = #{custId}

SELECT * FROM TB_CUST_ACCT
WHERE ACCT_CUST_CODE = #{custCode}
ORDER BY ACCT_REG_DT

mapper.update:
UPDATE TB_CUST_ACCT
SET ACCT_NAME = #{acctName}
WHERE ACCT_NO = #{acctNo}

UPDATE TB_CUST_FRGN_ACCT
SET FRGN_ACCT_NAME = #{acctName}
WHERE FRGN_ACCT_NO = #{acctNo}

UPDATE TB_CUST_ACCT
SET ACCT_BALANCE = ACCT_BALANCE - #{amount}
WHERE ACCT_NO = #{acctNo}

UPDATE TB_CUST_ACCT
SET ACCT_BALANCE = ACCT_BALANCE + #{amount}
WHERE ACCT_NO = #{acctNo}

UPDATE TB_CUST_INFO
SET
CUST_ZIP = #{custZip},
CUST_ADDR1 = #{custAddr1},
CUST_ADDR2 = #{custAddr2}


    , CUST_PW = #{custPw}


WHERE CUST_CODE = #{custCode}

mapper.select:
select da.*, tca.ACCT_BALANCE as dpstHdrLinkedAcctBal from TB_DPST_ACCT_HDR da
JOIN TB_CUST_ACCT tca ON da.DPST_HDR_LINKED_ACCT_NO = tca.ACCT_NO
where dpst_hdr_acct_no=#{dpstHdrAcctNo}

mapper.update:
update TB_DPST_ACCT_HDR
set
DPST_HDR_ADD_PAY_CNT=#{dpstHdrAddPayCnt},
DPST_HDR_BALANCE=#{dpstHdrBalance}
where
DPST_HDR_ACCT_NO=#{dpstHdrAcctNo}

update TB_CUST_ACCT
set acct_balance=#{acctBalance}
where acct_no=#{acctNo}

update TB_FRGN_ACCT_BALANCE
set bal_balance=#{balBalance}
where bal_no=#{balNo}
