mapper.select:
SELECT * FROM TB_CUST_ACCT
WHERE ACCT_CUST_CODE = #{custCode}
ORDER BY ACCT_REG_DT

mapper.update:
UPDATE TB_CUST_ACCT
SET ACCT_NAME = #{acctName}
WHERE ACCT_NO = #{acctNo}

UPDATE TB_CUST_FRGN_ACCT
SET FRGN_ACCT_NAME = #{acctName}
WHERE FRGN_ACCT_NO = #{acctNo}

UPDATE TB_CUST_ACCT
SET ACCT_BALANCE = ACCT_BALANCE - #{amount}
WHERE ACCT_NO = #{acctNo}

UPDATE TB_CUST_ACCT
SET ACCT_BALANCE = ACCT_BALANCE + #{amount}
WHERE ACCT_NO = #{acctNo}
